mapper.select:
select a.id as id, a.project_name as projectName, a.project_desc as projectDesc, a.create_time as createTime, b.role as role, (select u.user_name from t_user u where u.status = 1 and u.id = a.user_id) as userName from t_project a, t_project_member b where a.id = b.project_id and b.user_id = #{userId, jdbcType=BIGINT}
and a.project_name LIKE CONCAT('%', #{projectName, jdbcType=VARCHAR}, '%')
and a.status = 1 and b.status = 1 order by a.create_time desc

select a.id as memberId, a.user_id as userId, a.role as role, a.create_time as createTime, b.user_name as userName from t_project_member a, t_user b where a.status = 1 and b.status = 1 and a.user_id = b.id and a.project_id = #{projectId, jdbcType=BIGINT}
and b.user_name LIKE CONCAT('%', #{userName, jdbcType=VARCHAR}, '%')
ORDER BY a.create_time desc
