mapper.select:
SELECT uo.id, o.node_name AS nodeName, o.identity_id as identityId, o.identity_ip as identityIp, o.identity_port identityPort, o.public_flag as publicFlag, IFNULL(uo.valid_flag,1) as validFlag, uo.create_time as createTime, uo.update_time as updateTime FROM t_user_org_maintain uo INNER JOIN t_organization o ON uo.identity_id = o.identity_id WHERE uo.address = #{address, jdbcType=VARCHAR}
AND o.node_name = #{orgName, jdbcType=VARCHAR}
AND o.public_flag = 0 AND o.`status`=1 AND uo.`status`=1 union ALL SELECT 0 as id, o.node_name AS nodeName, o.identity_id as identityId, o.identity_ip as identityIp, o.identity_port identityPort, o.public_flag as publicFlag, 1 as validFlag, o.create_time as createTime, o.update_time as updateTime FROM t_organization o
