mapper.select:
select mw.workflow_id, mw.workflow_version, mw.workflow_name, mw.algorithm_name, mw.algorithm_id, mw.create_time, mw.last_run_time, mw.is_setting_completed, mw.create_mode, mw.calculation_process_step, mw.calculation_process_id, mw.calculation_process_name, (select mcps.type from mo_calculation_process_step mcps where mcps.calculation_process_id = mw.calculation_process_id and mcps.step = mw.calculation_process_step ) as calculation_process_step_type from mo_workflow mw where mw.address = #{address}
and mw.workflow_name like concat('%', #{keyword}, '%')
and mw.algorithm_id = #{algorithmId}
and mw.create_time >= #{begin}
and mw.create_time <= #{end}
and mw.create_mode = #{createMode}
